mapper.select:
select ioi.id,ioi.supplier_id,ioi.sku_id, ioi.sku_name, ioi.model,spec,ioi.attribute1,ioi.attribute2,ioi.attribute3, ioi.supply_price,ioi.quantity,ioi.status,ioi.send_time,ioi.pay_status, ifnull(iofee.other_fee,0.00) other_fee, io.contract_code,io.order_code from indent_order_item ioi LEFT JOIN indent_order io ON ioi.order_id = io.id left join ( select item_id,sum(fee_value) other_fee from ident_order_item_otherfee group by item_id) iofee on ioi.id = iofee.item_id
ioi.supplier_id IN
#{supplierId}
AND ioi.send_time >= #{startTime}
AND DATE_FORMAT(ioi.send_time,'%Y-%m-%d') <= #{endTime}
AND ioi.pay_status = 'NOT_PAIED' AND ioi.status = 'SENT'

mapper.update:
update indent_order_item
SET pay_status = 'PAIED',pay_time = #{payTime},operator = #{operator}
where id IN

      #{id}

mapper.select:
select ioi.id,ioi.supplier_id,ioi.sku_id, ioi.sku_name, ioi.model,spec,ioi.attribute1,ioi.attribute2,ioi.attribute3, ioi.supply_price,ioi.quantity,ioi.status,ioi.send_time,ioi.pay_status,ioi.operator, ifnull(iofee.other_fee,0.00) other_fee, io.contract_code,io.order_code,au.name from indent_order_item ioi LEFT JOIN indent_order io ON ioi.order_id = io.id LEFT JOIN acct_user au ON ioi.operator = au.id left join ( select item_id,sum(fee_value) other_fee from ident_order_item_otherfee group by item_id) iofee on ioi.id = iofee.item_id
ioi.supplier_id IN
#{supplierId}
AND (io.contract_code = #{keyword} OR io.order_code = #{keyword} OR ioi.sku_name = #{keyword})
AND ioi.pay_time >= #{startTime}
AND DATE_FORMAT(ioi.pay_time,'%Y-%m-%d') <= #{endTime}
AND ioi.pay_status = 'PAIED' AND ioi.status = 'SENT'
