mapper.select:
select psar.id, psar.approval_account, psar.approval_result, psar.approval_note, psar.sku_id, psar.approval_time,psar.approval_node, au.name from prod_sku_approval_record psar LEFT JOIN acct_user au ON au.id=psar.approval_account where psar.sku_id = #{skuId}
AND psar.approval_node = #{status}
ORDER BY psar.approval_time DESC

select
count(*)
from prod_sku_approval_record
where sku_id = #{skuId}
AND approval_node = #{status}
AND approval_result= '0'
